(* ::Subsubsubsection::Closed:: *)
(*overrideMonitor*)



overrideMonitor//Clear


overrideMonitor[expr_, sym_]:=
  Block[{Monitor},
    SetAttributes[Monitor, HoldAllComplete];
    Monitor[a_, b_, ___]:=
      (
        sym=b;
        a
        );
    expr;
    ];
overrideMonitor~SetAttributes~HoldAll;


(* ::Subsubsubsection::Closed:: *)
(*buildNotebookDocsSite*)



$protector=False;


buildNotebookDocsSite[loc_]:=
  With[{l=docsSiteLoc[loc]},
    Block[{blech},
      If[DirectoryQ@l,
        Module[{nb},
          nb=
            MessageDialog[
              Column@{
                StringForm[
                  "Creating site @ ``", l
                  ],
                Panel[
                  Dynamic[
                    If[MatchQ[blech, _Symbol], "", blech],
                    TrackedSymbols:>{blech}
                    ],
                  ImageSize->{350, 100}
                  ]
                },
              WindowTitle->"Building Site",
              WindowFloating->True
              ];
          overrideMonitor[
            PySimpleServerOpen@
              Ems["Build", l],
            blech
            ];
          NotebookClose@nb;
          ],
        $Failed
        ]
      ]
    ]


