*)



SaveNotebookToDocumentation//Clear;
SaveNotebookToDocumentation[nb_, proj_:Automatic]:=
  Module[
    {
      projName=proj,
      projDir,
      projConf,
      fname
      },
    projName=getNBProjectName[nb, projName];
    fname=getPacletSaveFileName[projName, nb];
