        ];
      CopyFile[
        PackageFilePath["FrontEnd", "StyleSheets", "SimpleDocs", "SimpleDocs.nb"],
        FileNameJoin@{
          Nest[DirectoryName, fname, 3],
          "SimpleDocsStyles.nb"
          },
        OverwriteTarget->True
        ];
      (*Quiet@*)Export[fname, prepNotebookForDocs[nb]]
      ]
    ]


